import 'package:flutter/material.dart';import 'package:font_awesome_flutter/font_awesome_flutter.dart';class HomePage extends StatefulWidget {  @override  _HomePageState createState() => _HomePageState();}class _HomePageState extends State<HomePage> {  int _index = 0;  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.grey,      body: Container(),      bottomNavigationBar: BottomNavigationBar(        type: BottomNavigationBarType.fixed,        backgroundColor: Colors.white,        showUnselectedLabels: true,        selectedItemColor: Colors.deepOrange,        unselectedItemColor: Colors.black,        elevation: 10,        currentIndex: _index,        onTap: (index){          setState(() {            _index = index;          });        },        items: [          BottomNavigationBarItem(              icon: Icon(Icons.explore), label: 'Explore'),          BottomNavigationBarItem(icon: Icon(Icons.search), label: 'Search'),          BottomNavigationBarItem(              icon: Icon(Icons.add_circle_outline,size: 50,color: Colors.deepOrange,), label: ''),          BottomNavigationBarItem(              icon: Icon(Icons.account_circle), label: 'Account'),          BottomNavigationBarItem(              icon: Icon(Icons.contactless_outlined), label: 'Announcement'),        ],      ),    );  }}