import 'package:flutter/material.dart';import 'package:flutter_new_travel_app/widgets.dart';import 'package:google_fonts/google_fonts.dart';class SignUpPage extends StatelessWidget {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: Colors.black45,      body: Stack(        children: [          Container(            height: MediaQuery.of(context).size.height,            width: MediaQuery.of(context).size.width,            decoration: BoxDecoration(                color: Colors.red,                image: DecorationImage(                  image: AssetImage('assets/map_blur.jpg'),                  fit: BoxFit.cover,                )            ),          ),          ListView(            shrinkWrap: true,            physics: ClampingScrollPhysics(),            children: [              Container(                alignment: Alignment.centerLeft,                margin: EdgeInsets.only(left: 10, top: 20),                child: Icon(                  Icons.arrow_back_rounded,                  color: Colors.white,                ),              ),              Container(                margin: EdgeInsets.only(left: 10,top: 20,bottom: 20),                child: Text('New Account',style: GoogleFonts.kiteOne(                    color: Colors.white,fontWeight: FontWeight.w700,fontSize: 25                ),),              ),              Padding(                padding: const EdgeInsets.all(10.0),                child: TextFormFieldWidget(                  name: 'First Name',                ),              ),              Padding(                padding: const EdgeInsets.all(10.0),                child: TextFormFieldWidget(                  name: 'Last Name',                ),              ),              Padding(                padding: const EdgeInsets.all(10.0),                child: TextFormFieldWidget(                  name: 'Email',                ),              ),              Padding(                padding: const EdgeInsets.all(10.0),                child: TextFormFieldWidget(                  name: 'Password',                ),              ),              Padding(                padding: const EdgeInsets.all(10.0),                child: TextFormFieldWidget(                  name: 'Confirm Password',                ),              ),              Container(                margin: EdgeInsets.only(top: 50),                child: GradientButtonWidget(                  textTitle: 'Sign up',                  color1: Colors.deepOrange,                  color2: Colors.deepOrangeAccent,                  opacity: 1,                  textColor: Colors.white,                ),              )            ],          ),        ],      )    );  }}class TextFormFieldWidget extends StatelessWidget {  final String name;  const TextFormFieldWidget({Key key, this.name}) : super(key: key);  @override  Widget build(BuildContext context) {    return TextFormField(      decoration: InputDecoration(          filled: true,          fillColor: Colors.white,          hintText: name,          contentPadding: EdgeInsets.only(left: 20, right: 20),          border: OutlineInputBorder(              borderRadius: BorderRadius.circular(25),              borderSide: BorderSide(                  color: Colors.white              )          ),        labelStyle: TextStyle(color: Colors.black87),        focusedBorder: OutlineInputBorder(            borderRadius: BorderRadius.circular(25),            borderSide: BorderSide(              color: Colors.white,            )        ),        enabledBorder: OutlineInputBorder(            borderRadius: BorderRadius.circular(25),            borderSide: BorderSide(              color: Colors.white,            )        ),      ),    );  }}